<?php	class ConfigGenerator	{		private $data;				public function Init()		{			$this->data = "<?php\r\n";			$this->data .= "/* Generated by EVEmu Portal Install Script. Please do not modify */\r\n";		}				public function AddVariable($name, $value)		{			$this->data .= "$" . $name . "=\"" . $value . "\";" . "\r\n";		}				public function GetResult()		{			return $this->data;		}				public function SaveFile($file)		{			$fp = fopen($file, "wb");						fwrite($fp, $this->data);						fclose($fp);		}				public function Stop()		{			$this->data .= "?>\r\n";		}	}?>